import React from "react"
import { createField, Input, Textarea } from "../../common/FormsControls/FormsControls"
import { reduxForm } from "redux-form"
import s from './ProfileInfo.module.css';
import style from "../../common/FormsControls/FormsControls.module.css"

const ProfileDataForm = ({ handleSubmit, profile, error }) => {
    return <form onSubmit={handleSubmit}>
        <div>
            <button>Save</button>
        </div>
        {error && <div className={styleMedia.formSummaryError}>
            {error}
        </div>}
        <b>Fullname</b>: {createField("Full name", "fullName", [], Input)}
        < div >
            <b>Looking for a job</b>:
             {createField("", "lookingForAJob", [], Input, { type: "checkbox" })}
        </div>
        <div>
            <b>My proffesional skills</b>:
                {createField("My proffesional skills", "lookingForAJobDescription", [], Textarea)}
        </div>

        <div>
            <b>About me</b>
            {createField("About me", "aboutMe", [], Textarea)}
        </div>
        <div>
            <b>Contacts</b>: {Object.keys(profile.contacts).map(key => {
                return <div key={key} className={s.contact}>
                    <b>{key}: {createField(key, "contacts." + key, [], Input)}</b>
                </div>
            })}
        </div>
    </form>
}

const ProfileDataFormReduxForm = reduxForm({form:"edit-profle"})(ProfileDataForm)


export default ProfileDataFormReduxForm
